import math
from numpy import fft as fft_np
import gnuradio.digital.psk as psk
import gnuradio.digital.qam as qam


def gen_framer_info(modulation):
    mods = {"bpsk": 2, "qpsk": 4, "8psk": 8, "qam8": 8, "qam16": 16, "qam64": 64, "qam256": 256}
    arity = mods[modulation]

    rot = 1
    if modulation == "qpsk":
        rot = (0.707+0.707j)

    # FIXME: pass the constellation objects instead of just the points
    if(modulation.find("psk") >= 0):
        constel = psk.psk_constellation(arity)
        rotated_const = map(lambda pt: pt * rot, constel.points())
    elif(modulation.find("qam") >= 0):
        constel = qam.qam_constellation(arity)
        rotated_const = map(lambda pt: pt * rot, constel.points())

    return rotated_const


def gen_preamble_data(fftlen, occupied_carriers):
    # Use freq domain to get doubled-up known symbol for correlation in time domain
    zeros_on_left = int(math.ceil((fftlen - occupied_carriers)/2.0))
    ksfreq = known_symbols_4512_3[0:occupied_carriers]
    for i in range(len(ksfreq)):
        if((zeros_on_left + i) & 1):
            ksfreq[i] = 0

    # hard-coded known symbols
    preambles = (ksfreq,)

    ks0 = fftlen*[0,]
    ks0[zeros_on_left : zeros_on_left + occupied_carriers] = preambles[0]
    ks0 = fft_np.ifftshift(ks0)
    # return ks0
    return preambles[0]

